void main() {
  final int part1Result = part1();
  final int part2Result = part2();
  
  assert(part1Result == 1542);
  assert(part2Result == 3153);
  
  print('part1: $part1Result');
  print('part2: $part2Result');
}

int part1() {
  return findFirstMarker(4);
}

int part2() {
  return findFirstMarker(14);
}

int findFirstMarker(int nDistinctChars) {
  final String datastream = input();
  for (int i = 0; i < datastream.length - nDistinctChars -1; i++) {
    final String chunk = datastream.substring(i, i + nDistinctChars);
    if (containsUniqueChars(chunk)) {
      return i + nDistinctChars;
    }
  }
  return -1;
}

bool containsUniqueChars(String input) {
  return input.split('').toSet().length == input.length;
}

String input() {
  return '';
}